import { Fragment, useContext} from 'react';
import { Outlet, Link } from 'react-router-dom';

import { ReactComponent as CrownLogo } from '../../assets/crown.svg';

import CartIcon from '../../components/cart-icon/cart-icon.component';
import CartDropdown from '../../components/cart-dropdown/cart-dropdown.component';

import { UserContext } from '../../contexts/user.context';
import { CartContext } from '../../contexts/cart.context';
import { ClickingContext } from '../../contexts/clicking.context';


import { signOutUser } from '../../utils/firebase/firebase.utils';

import './navigation.styles.scss'

const Navigation = () => {
	const { currentUser } = useContext(UserContext);
	const { isCartOpen } = useContext(CartContext);
	const { clickingCount } = useContext(ClickingContext);

  return(
    <Fragment>
    	<div className = 'navigation'>

				<Link className = 'logo-container' to ='/'>
						<CrownLogo className = 'logo' />
				</Link>

				<Span>{clickingCount}</Span>

				<div className = 'nav-links-container'>
					<Link className = 'nav-link' to ='/shop'>
						SHOP
					</Link>

					{ currentUser ? (
						<span className='nav-link' onClick={signOutUser}>SIGN OUT </span>
						) : (
							<Link className = 'nav-link' to ='/auth'>SIGN IN</Link>
						)
					}
					<CartIcon/>
	    	</div>
				{ isCartOpen &&	 <CartDropdown />} 
						{/*if this total thing is true (isCartOpen is true and CartIcon (all component == true by default) i will return the last thing you gave me (CartDropdown))*/}	   
      </div>
      <Outlet />
    </Fragment>
  );

};

export default Navigation;
