import { createContext, useState } from 'react';

export const ClickingContext = createContext({
	clickingCount: 0,
	setClickingCount: () => null,
});

export const ClickingProvider = ({children}) => {
	const [clickingCount, setClickingCount] = useState(0);
	const value = { clickingCount, setClickingCount };
	return <ClickingContext.Provider value={value}>{children}</ClickingContext.Provider>
};
