import { useContext } from 'react';

import { ProductCardContainer, ProductCardImage, ButtonStyled, Footer, Name, Price } from './product-card.styles';

import Button, { BUTTON_TYPE_CLASSES } from '../button/button.component';
import { CartContext } from '../../contexts/cart.context';

const ProductCard = ({ product }) => {
	const { name, price, imageUrl} = product;
	const { addItemToCart } = useContext(CartContext);
	const addProductToCart = () => addItemToCart(product);
	return(
		<ProductCardContainer>
			<ProductCardImage src={imageUrl} alt={`${name}`} />
			<Footer>
				<Name>{name}</Name>
				<Price>${price}</Price>
			</Footer>
			<ButtonStyled buttonType={BUTTON_TYPE_CLASSES.inverted} onClick={addProductToCart}>Add to Cart</ButtonStyled>
		</ProductCardContainer>
	);
};

export default ProductCard;
