import styled from 'styled-components';

export const CategoryTitle = styled.h2`
   font-size: 38px;
   margin-bottom: 50px;
   text-align: center;

`
export const CategoryContainer = styled.div`
	display: grid;
	grid-template-columns: repeat(4, 1fr);
	column-gap: 20px;
	row-gap: 50px;
`
